import React, { useState } from "react";
const [text, setText] = useState("Enter the text here");
setText="mdahbdasbdand";
export default class textform2 extends PureComponent {
  

  render() {
    return (
      <>
        <div>
          <h1>{props.heading}</h1>
          <div className="mb-5">
            <label htmlFor="exampleFormControlTextarea1" className="form-label">
              ENTER THE TEXT
            </label>
            <textarea
              value={text}
              onChange={handleChange}
              className="form-control"
              id="exampleFormControlTextarea1"
              placeholder="Enter the text"
              style={{
                height: "120px", // Increased height for multi-line input
                fontSize: "16px", // Adjusted font size
                resize: "none", // Prevent resizing if desired
              }}
            />
          </div>
        </div>
        <button className="btn btn-primary mx-1" onClick={handleUppercase}>
          Convert to Uppercase
        </button>
        <button className="btn btn-primary mx-1" onClick={handleLowercase}>
          Convert to Lowercase
        </button>
      </>
    );
  }
}
