import React, { useState } from "react";

export default function TextForm(props) {
  const [text, setText] = useState("Enter the text here");
  const [count, setCount] = useState(0);
  const [font, setFont] = useState("Arial");
  const [color, setColor] = useState("Black");
  const [value, setValue] = useState("1");

  const handleUppercase = () => {
    setText(text.toUpperCase());
  };

  const handleLowercase = () => {
    setText(text.toLowerCase());
  };

  const handleChange = (event) => {
    setText(event.target.value);
  };

  const handleClear = () => {
    setText("");
  };

  const handleIncrement = () => {
    setCount(count + 1);
  };

  const handleDecrement = () => {
    setCount(count - 1); 
  };

  const handleFontChange = (event) => {
    setFont(event.target.value); 
  };

  const handleColorChange = (event) => {
    setColor(event.target.value); 
  };
  const handlesizeChange = (event) => {
    setValue(event.target.value); 
  }

  return (
    <>
      <div>
        <h1>{props.heading || "Default Heading"}</h1>
        <div className="mb-3">
          <label htmlFor="exampleFormControlTextarea1" className="form-label">
            Enter the Text
          </label>
          <textarea
            value={text}
            onChange={handleChange}
            className="form-control"
            id="exampleFormControlTextarea1"
            placeholder="Enter the text"
            style={{
              height: "120px",
              fontSize: "16px",
              resize: "none",
              borderColor: "black",
              fontFamily: font,
              color: color, 
            }}
          />
        </div>


        {/* <div>
        <h1>Hindi</h1>
        <div className="mb-3">
          <label htmlFor="exampleFormControlTextarea1" className="form-label">
            Hindi converted Text
          </label>
          <textarea
            value={text}
            onChange={handleChange}
            className="form-control"
            id="exampleFormControlTextarea1"
            placeholder="Enter the text"
            style={{
              height: "120px",
              fontSize: "16px",
              resize: "none",
              borderColor: "black",
              fontFamily: font,
              color: color, // Set the color dynamically
            }}
          />
        </div>
          </div> */}
        <div>
          <label htmlFor="fontSelect" className="form-label m-2">
            Select Font:
          </label>
          <select
            id="fontSelect"
            className="form-select"
            value={font}
            onChange={handleFontChange}
          >
            <option value="Arial">Arial</option>
            <option value="Verdana">Verdana</option>
            <option value="Helvetica">Helvetica</option>
            <option value="Times New Roman">Times New Roman</option>
            <option value="Georgia">Georgia</option>
            <option value="Courier New">Courier New</option>
            <option value="Comic Sans MS">Comic Sans MS</option>
            <option value="Poppins">Poppins</option>
            <option value="Roboto">Roboto</option>
          </select>
        </div>

        <div>
          <label htmlFor="fontSelectsize" className="form-label m-2">
            Select Font Size:
          </label>
          <select
            id="fontSelect"
            className="form-select"
            value={value}
            onChange={handlesizeChange}
          >
            <option value="1" style={fontSize='small'}><p1>Heading</p1></option>
            <option value="2" style={fontSize='medium'}><p2>Heading</p2></option>
            <option value="3" style={fontSize='large'}><p3>Heading</p3></option>
            <option value="4"><p4>Heading</p4></option>
          </select>
        </div>


        <div>
          <label htmlFor="Colorselect" className="form-label-5 m-2">
            Select Text Color:
          </label>
          <select
            id="Colorselect"
            className="form-select"
            value={color}
            onChange={handleColorChange}
          >
            <option value="Red" style={{ color: "red" }}>Red</option>
            <option value="Blue" style={{ color: "blue" }}>Blue</option>
            <option value="Green" style={{ color: "green" }}>Green</option>
            <option value="Pink" style={{ color: "pink" }}>Pink</option>
            <option value="Black" style={{ color: "black" }}>Black</option>
          </select>
        </div>
      </div>
      <div className="m-3" >
      <button className="btn btn-primary mx-3" onClick={handleUppercase}>
        Convert to Uppercase
      </button>

      <button className="btn btn-primary mx-1" onClick={handleLowercase}>
        Convert to Lowercase
      </button>

      <button className="btn btn-primary mx-1" onClick={handleIncrement}>
        Increment
      </button>

      <button className="btn btn-primary mx-1" onClick={handleDecrement}>
        Decrement
      </button>

      <button className="btn btn-danger mx-1" onClick={handleClear}>
        Clear
      </button>
      </div>
      <div>
        <h2>Text Analysis Results</h2>
        <p>
          Words={text.trim().split(/\s+/).filter(Boolean).length} and
          Characters={text.length}
        </p>
        <p>Count: {count}</p>
      </div>
    </>
  );
}
