import React from "react";
import "./App.css";
import Body from "./Components/Body";  // Ensure correct casing
import Textform from "./Components/TextForm"; // Ensure correct casing

function App() {
  return (
    <>
      <div className="container">
        <Textform heading="Enter the text to analyze" />
      </div>
      <br />
      <div className="container m-2">
        <Body />
      </div>
    </>
  );
}

export default App;
